import React, { useState } from 'react';
import '../ChatView.css';

// Импорты иконок
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import AttachFileIcon from '@mui/icons-material/AttachFile';

function ChatView({ chatId, onBack }) {
  const [messages, setMessages] = useState([]); // Список сообщений
  const [inputValue, setInputValue] = useState(''); // Текущее сообщение
  const [menuVisible, setMenuVisible] = useState(false); // Состояние меню

  // Функция отправки сообщения
  const sendMessage = () => {
    if (inputValue.trim() !== '') {
      setMessages([...messages, { text: inputValue, sender: 'user' }]);
      setInputValue('');
    }
  };

  // Функция переключения видимости меню
  const toggleMenu = () => {
    setMenuVisible((prev) => !prev);
  };

  return (
    <div className="chat-container">
      {/* Заголовок чата */}
      <div className="chat-header">
        {/* Иконка "Назад" */}
        <span className="material-icons back-icon" onClick={onBack}>
          <ArrowBackIcon />
        </span>
        <img
          src="https://via.placeholder.com/40"
          alt="avatar"
          className="avatar"
          style={{ borderRadius: '50%', marginRight: '10px' }}
        />
        <div className="profile-info">
          <div className="name">Дима Масалов</div>
          <div className="status">Был в сети 2 часа назад</div>
        </div>
        <div className="menu-icons">
          {/* Иконка "Поиск" */}
          <span className="material-icons">
            <SearchIcon />
          </span>
          {/* Иконка "Еще" */}
          <span
            className="material-icons more-icon"
            onClick={toggleMenu}
          >
            <MoreVertIcon />
          </span>
          {/* Всплывающее меню */}
          {menuVisible && (
            <div className="dropdown-menu">
              <div>Info</div>
              <div>Mute</div>
            </div>
          )}
        </div>
      </div>

      {/* Список сообщений */}
      <div className="message-list" id="messageList">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message ${
              message.sender === 'user' ? 'sent' : 'received'
            }`}
          >
            {message.text}
          </div>
        ))}
      </div>

      {/* Поле ввода сообщения */}
      <div className="chat-input">
        {/* Иконка "Прикрепить файл" */}
        <span className="material-icons attach-icon">
          <AttachFileIcon />
        </span>
        <input
          type="text"
          id="messageInput"
          placeholder="Введите сообщение"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === 'Enter') sendMessage();
          }}
        />
        <button id="sendMessageButton" onClick={sendMessage}>
          Отправить
        </button>
      </div>
    </div>
  );
}

export default ChatView;
