import React, { useState } from 'react';
import { TextField, IconButton, Avatar } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import CheckIcon from '@mui/icons-material/Check';
import CameraAltIcon from '@mui/icons-material/CameraAlt';
import { useNavigate } from 'react-router-dom';
import '../ProfilePage.css';

function ProfilePage() {
  const navigate = useNavigate();

  // Локальное состояние для управления данными профиля
  const [profile, setProfile] = useState({
    fullName: 'Tony Jackobson',
    username: '@tony',
    bio: 'Subtitle 1',
  });

  const handleChange = (field, value) => {
    setProfile((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <div className="profile-page">
      {/* Верхняя панель */}
      <div className="header">
        <IconButton onClick={() => navigate(-1)}>
          <ArrowBackIcon style={{ color: 'white' }} />
        </IconButton>
        <h2>Редактировать профиль</h2>
        <IconButton>
          <CheckIcon style={{ color: 'white' }} />
        </IconButton>
      </div>

      {/* Аватар */}
      <div className="avatar-container">
        <Avatar className="avatar" alt="Avatar" src="" style={{ width: 100, height: 100 }}>
          <IconButton className="camera-icon">
            <CameraAltIcon />
          </IconButton>
        </Avatar>
      </div>

      {/* Поля профиля */}
      <form className="profile-form">
        {/* Полное имя */}
        <div className="form-field">
          <TextField
            label="Полное имя"
            variant="outlined"
            fullWidth
            value={profile.fullName}
            onChange={(e) => handleChange('fullName', e.target.value)}
          />
        </div>

        {/* Имя пользователя */}
        <div className="form-field">
          <TextField
            label="Имя пользователя"
            variant="outlined"
            fullWidth
            value={profile.username}
            onChange={(e) => handleChange('username', e.target.value)}
            helperText="Минимальная длина: 5 символов"
          />
        </div>

        {/* Bio */}
        <div className="form-field">
          <TextField
            label="О себе"
            variant="outlined"
            multiline
            rows={4}
            fullWidth
            value={profile.bio}
            onChange={(e) => handleChange('bio', e.target.value)}
            helperText="Любая информация о вас"
          />
        </div>
      </form>
    </div>
  );
}

export default ProfilePage;
