import React from 'react';
import '../ChatView.css';

// Импорты иконок
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import AttachFileIcon from '@mui/icons-material/AttachFile';

function ChatView({ chatId, onBack }) {
  return (
    <div className="chat-container">
      <div className="chat-header">
        {/* Иконка "Назад" */}
        <span className="material-icons back-icon" onClick={onBack}>
          <ArrowBackIcon />
        </span>
        <img
          src="https://via.placeholder.com/40"
          alt="avatar"
          className="avatar"
          style={{ borderRadius: '50%', marginRight: '10px' }}
        />
        <div className="profile-info">
          <div className="name">Дима Масалов</div>
          <div className="status">Был в сети 2 часа назад</div>
        </div>
        <div className="menu-icons">
          {/* Иконка "Поиск" */}
          <span className="material-icons">
            <SearchIcon />
          </span>
          {/* Иконка "Еще" */}
          <span className="material-icons more-icon">
            <MoreVertIcon />
          </span>
          <div className="dropdown-menu" id="dropdownMenu">
            <div>Info</div>
            <div>Mute</div>
          </div>
        </div>
      </div>
      <div className="message-list" id="messageList">
        Сообщения
      </div>
      <div className="chat-input">
        {/* Иконка "Прикрепить файл" */}
        <span className="material-icons attach-icon">
          <AttachFileIcon />
        </span>
        <input type="text" id="messageInput" placeholder="Введите сообщение" />
        <button id="sendMessageButton">Отправить</button>
      </div>
    </div>
  );
}

export default ChatView;
