import React, { useState, useEffect } from 'react';
import './ChatView.css';

function ChatView({ chat, onBack }) {
  const [messages, setMessages] = useState([]);
  const [messageText, setMessageText] = useState('');

  useEffect(() => {
    const storedMessages = JSON.parse(localStorage.getItem(`messages-${chat.id}`) || '[]');
    setMessages(storedMessages);
  }, [chat.id]);

  const addMessage = (text) => {
    const newMessage = { text, type: 'sent', time: new Date().toLocaleTimeString() };
    const updatedMessages = [...messages, newMessage];
    setMessages(updatedMessages);
    localStorage.setItem(`messages-${chat.id}`, JSON.stringify(updatedMessages));
  };

  const handleSendMessage = () => {
    if (messageText.trim()) {
      addMessage(messageText.trim());
      setMessageText('');
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <span className="material-icons back-icon" onClick={onBack}>arrow_back</span>
        <img src={chat.avatar} alt="avatar" className="avatar" style={{ borderRadius: '50%', marginRight: '10px' }} />
        <div className="profile-info">
          <div className="name">{chat.name}</div>
          <div className="status">Был в сети недавно</div>
        </div>
        <div className="menu-icons">
          <span className="material-icons">search</span>
          <span className="material-icons more-icon">more_vert</span>
          <div className="dropdown-menu" id="dropdownMenu">
            <div>Info</div>
            <div>Mute</div>
          </div>
        </div>
      </div>
      <div className="message-list">
        {messages.map((msg, index) => (
          <div key={index} className={`message-item ${msg.type}`}>
            [{msg.time}] {msg.text}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <span className="material-icons attach-icon">attach_file</span>
        <input
          type="text"
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          placeholder="Введите сообщение"
        />
        <button onClick={handleSendMessage}>Отправить</button>
      </div>
    </div>
  );
}

export default ChatView;
