import React, { useState } from 'react';
import ChatList from './ChatList'; // Подключаем компонент
import ChatView from './ChatView'; // Подключаем компонент чата
import './App.css';

function App() {
  const [activeChat, setActiveChat] = useState(null); // Стейт для отслеживания выбранного чата

  // Обработчик для открытия чата
  const handleChatSelect = (chatId) => {
    setActiveChat(chatId);
  };

  return (
    <div className="App">
      {activeChat ? (
        // Показать компонент ChatView, если выбран чат
        <ChatView chatId={activeChat} onBack={() => setActiveChat(null)} />
      ) : (
        // Показать компонент ChatList, если чат не выбран
        <ChatList onSelectChat={handleChatSelect} />
      )}
    </div>
  );
}

export default App;
