import React from 'react';
import '../ChatView.css';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import MoreVertIcon from '@mui/icons-material/MoreVert';

function ChatView({ chatId, onBack }) {
  return (
    <div className="chat-container">
      <div className="chat-header">
        <span className="material-icons back-icon" onClick={onBack}>arrow_back</span>
        <img src="https://via.placeholder.com/40" alt="avatar" className="avatar" style={{ borderRadius: '50%', marginRight: '10px' }} />
        <div className="profile-info">
          <div className="name">Дима Масалов</div>
          <div className="status">Был в сети 2 часа назад</div>
        </div>
        <div className="menu-icons">
          <span className="material-icons">search</span>
          <span className="material-icons more-icon">more_vert</span>
          <div class="dropdown-menu" id="dropdownMenu">
                    <div>Info</div>
                    <div>Mute</div>
          </div>
        </div>
      </div>
      <div className="message-list" id="messageList">Сообщения</div>
      <div className="chat-input">
        <span className="material-icons attach-icon">attach_file</span>
        <input type="text" id="messageInput" placeholder="Введите сообщение" />
        <button id="sendMessageButton">Отправить</button>
      </div>
    </div>
  );
}

export default ChatView;
