import React, { useState } from 'react';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import ChatIcon from '@mui/icons-material/Chat';
import SearchIcon from '@mui/icons-material/Search';
import DoneIcon from '@mui/icons-material/Done';
import DoneAllIcon from '@mui/icons-material/DoneAll';
import EditIcon from '@mui/icons-material/Edit';
import { useNavigate } from 'react-router-dom';
import '../App.css';

// Данные чатов
const chatData = [
  {
    id: 1,
    name: 'Дима Масалов',
    message: 'Привет, как дела?',
    time: '10:45',
    status: 'done_all',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 2,
    name: 'Иван Петров',
    message: 'До встречи!',
    time: '09:30',
    status: 'done',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 3,
    name: 'Мария Смирнова',
    message: 'Спасибо за помощь!',
    time: '08:15',
    status: 'done_all',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 4,
    name: 'Алексей Кузнецов',
    message: 'img_12-12-24',
    time: '07:50',
    status: 'done_all',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 5,
    name: 'Юлия Федорова',
    message: 'Отправила документы.',
    time: '07:00',
    status: 'done',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 6,
    name: 'Дмитрий Соколов',
    message: 'Когда встречаемся?',
    time: '06:30',
    status: 'done_all',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 7,
    name: 'Ольга Попова',
    message: 'Хорошего дня!',
    time: '06:00',
    status: 'done',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 8,
    name: 'Егор Лебедев',
    message: 'Проверь почту.',
    time: '05:30',
    status: 'done_all',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 9,
    name: 'Виктория Григорьева',
    message: 'Отлично, до завтра.',
    time: '05:00',
    status: 'done',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 10,
    name: 'Сергей Никитин',
    message: 'Скоро выезжаю.',
    time: '04:30',
    status: 'done_all',
    avatar: 'https://via.placeholder.com/40',
  },
  {
    id: 11,
    name: 'Алина Крылова',
    message: 'Жду твой ответ.',
    time: '04:00',
    status: 'done',
    avatar: 'https://via.placeholder.com/40',
  },
];

// Компонент списка чатов
function ChatList({ onSelectChat }) {
  const navigate = useNavigate();
  const [drawerOpen, setDrawerOpen] = useState(false);

  // Функция для выбора иконки статуса
  const getStatusIcon = (status) => {
    switch (status) {
      case 'done':
        return <DoneIcon />;
      case 'done_all':
        return <DoneAllIcon />;
      default:
        return null;
    }
  };

  // Открытие и закрытие бокового меню
  const toggleDrawer = (open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setDrawerOpen(open);
  };

  return (
    <div className="chat-list-container">
      {/* Заголовок */}
      <div className="header">
        <IconButton onClick={toggleDrawer(true)}>
          <MenuIcon />
        </IconButton>
        <div className="header-title">Messenger</div>
        <IconButton>
          <SearchIcon />
        </IconButton>
      </div>

      {/* Боковое меню */}
      <Drawer anchor="left" open={drawerOpen} onClose={toggleDrawer(false)}>
        <List>
          <ListItem button onClick={() => navigate('/')}>
            <ListItemIcon>
              <ChatIcon />
            </ListItemIcon>
            <ListItemText primary="Чаты" />
          </ListItem>
          <ListItem button onClick={() => navigate('/profile')}>
            <ListItemIcon>
              <AccountCircleIcon />
            </ListItemIcon>
            <ListItemText primary="Профиль" />
          </ListItem>
        </List>
      </Drawer>

      {/* Список чатов */}
      <div className="chat-items">
        {chatData.map((chat) => (
          <div
            className="chat-item"
            key={chat.id}
            onClick={() => onSelectChat(chat)}
          >
            <img src={chat.avatar} alt="avatar" className="avatar" />
            <div className="chat-info">
              <div className="chat-name">{chat.name}</div>
              <div className="chat-message">{chat.message}</div>
            </div>
            <div className="chat-status">
              <span className="time">{chat.time}</span>
              <span className="status-icon">{getStatusIcon(chat.status)}</span>
            </div>
          </div>
        ))}
      </div>

      {/* Кнопка создания нового чата */}
      <div className="floating-button">
        <IconButton>
          <EditIcon />
        </IconButton>
      </div>
    </div>
  );
}

export default ChatList;
