import React from 'react';
import { HashRouter as Router, Routes, Route, Link } from 'react-router-dom'; // Подключаем HashRouter
import ChatList from './components/ChatList';
import ChatView from './components/ChatView';
import ProfilePage from './components/ProfilePage';
import '../App.css';

function App() {
  return (
    <Router>
      <div className="App">
        {/* Навигация для быстрого доступа к страницам */}
        <nav className="app-navigation">
          <Link to="/" className="nav-link">Список чатов</Link>
          <Link to="/profile" className="nav-link">Профиль</Link>
        </nav>

        {/* Настройка маршрутов */}
        <Routes>
          {/* Главная страница — список чатов */}
          <Route path="/" element={<ChatList />} />

          {/* Отдельный чат, доступ по ссылке /chat/:id */}
          <Route path="/chat/:id" element={<ChatView />} />

          {/* Страница профиля */}
          <Route path="/profile" element={<ProfilePage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
