import React, { useState } from 'react';
import '../ChatView.css';

// Импорты иконок
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import DoneIcon from '@mui/icons-material/Done';

function ProfilePage({ onBack }) {
  const [profileData, setProfileData] = useState({
    fullName: 'Tony Jackobson',
    username: '@tony',
    bio: 'Subtitle 1',
  });

  const handleChange = (field, value) => {
    setProfileData({ ...profileData, [field]: value });
  };

  return (
    <div className="chat-container">
      {/* Заголовок */}
      <div className="chat-header">
        {/* Иконка "Назад" */}
        <span className="material-icons back-icon" onClick={onBack}>
          <ArrowBackIcon />
        </span>
        <div className="profile-header-title">Edit Profile</div>
        {/* Иконка "Сохранить" */}
        <span className="material-icons save-icon">
          <DoneIcon />
        </span>
      </div>

      {/* Контент профиля */}
      <div className="profile-content">
        {/* Аватар */}
        <div className="profile-photo">
          <img
            src="https://via.placeholder.com/80"
            alt="profile"
            className="avatar"
          />
          <button className="upload-button">+</button>
        </div>

        {/* Поля редактирования */}
        <div className="profile-fields">
          {/* Полное имя */}
          <div className="form-group">
            <label>Full Name</label>
            <input
              type="text"
              value={profileData.fullName}
              onChange={(e) => handleChange('fullName', e.target.value)}
            />
          </div>

          {/* Имя пользователя */}
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              value={profileData.username}
              onChange={(e) => handleChange('username', e.target.value)}
            />
            <small>Minimum length is 5 characters</small>
          </div>

          {/* Био */}
          <div className="form-group">
            <label>Bio</label>
            <textarea
              value={profileData.bio}
              onChange={(e) => handleChange('bio', e.target.value)}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
