import React, { useState } from 'react';
import MenuIcon from '@mui/icons-material/Menu'; // Импорт значков из Material-UI
import SaveIcon from '@mui/icons-material/Check'; // Иконка сохранения профиля
import CameraAltIcon from '@mui/icons-material/CameraAlt'; // Иконка камеры
import '../ProfilePage.css'; // Импорт CSS для стилизации страницы

function ProfilePage() {
  // Используем состояние для хранения данных профиля
  const [fullName, setFullName] = useState('');
  const [username, setUsername] = useState('');
  const [bio, setBio] = useState('');

  // Обработчик для изменения данных в поле "Полное имя"
  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  // Обработчик для изменения данных в поле "Имя пользователя"
  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  // Обработчик для изменения данных в поле "О себе"
  const handleBioChange = (event) => {
    setBio(event.target.value);
  };

  // Функция сохранения профиля
  const handleSaveProfile = () => {
    console.log('Профиль сохранён:', { fullName, username, bio });
    alert('Изменения сохранены!');
  };

  return (
    <div className="profile-page">
      {/* Заголовок страницы */}
      <div className="profile-header">
        <button className="menu-button">
          <MenuIcon />
        </button>
        <h1 className="profile-title">Редактировать профиль</h1>
        <button className="save-button" onClick={handleSaveProfile}>
          <SaveIcon />
        </button>
      </div>

      {/* Фото профиля */}
      <div className="profile-photo">
        <div className="photo-circle">
          <CameraAltIcon className="camera-icon" />
        </div>
      </div>

      {/* Поле "Полное имя" */}
      <div className="profile-field">
        <label className="field-label">Полное имя</label>
        <input
          type="text"
          value={fullName}
          onChange={handleFullNameChange}
          placeholder="Введите полное имя"
          className="field-input"
        />
      </div>

      {/* Поле "Имя пользователя" */}
      <div className="profile-field">
        <label className="field-label">Имя пользователя</label>
        <input
          type="text"
          value={username}
          onChange={handleUsernameChange}
          placeholder="Введите имя пользователя"
          className="field-input"
        />
        <p className="field-helper-text">Минимальная длина — 5 символов</p>
      </div>

      {/* Поле "О себе" */}
      <div className="profile-field">
        <label className="field-label">О себе</label>
        <textarea
          value={bio}
          onChange={handleBioChange}
          placeholder="Расскажите немного о себе"
          className="field-textarea"
        />
      </div>
    </div>
  );
}

export default ProfilePage;
