import React from 'react';


function ChatView({ chat, onBack }) {
  return (
    <div className="chat-container">
      <div className="chat-header">
        <span className="material-icons back-icon" onClick={onBack}>arrow_back</span>
        <img src={chat.avatar} alt="avatar" className="avatar" />
        <div className="profile-info">
          <div className="name">{chat.name}</div>
          <div className="status">Был в сети недавно</div>
        </div>
      </div>
      <div className="message-list">
        {/* Содержимое сообщений можно добавить здесь */}
      </div>
      <div className="chat-input">
        <input type="text" placeholder="Введите сообщение" />
        <button>Отправить</button>
      </div>
    </div>
  );
}

export default ChatView;
